import { useNavigate } from "react-router-dom";
import { useState, useEffect } from 'react';
import { All } from '../../API/usuarios'
import { Nav } from "../Nav"
import { Sidebar } from "../Sidebar"
import ModalForm from './Modal'
import Tabla from "./Tabla"

export function Usuarios() {
    const navigate = useNavigate()

    const [showDiv, setShowDiv] = useState(false);

    const toggleDiv = () => {
        setShowDiv(!showDiv);
    };

    const [usuario, setUsuario] = useState([{
        user_id: 0,
        nombre: "",
        direccion: "",
        usuario_admin: 0,
        nombre_de_usuario: "",
        contrasenia: ""
    }]);

    useEffect(() => {
        refrescarInformacion()
    }, []);

    const refrescarInformacion = () => {
        All()
            .then(async res => await res)
            .then(
                (result) => {
                    console.log(result)
                    setUsuario(result);
                },
                (error) => {
                    console.log(error);
                }
            )
    };


    return (
        <><Nav /><section class="flex flex-row w-full">
            <div>
                <Sidebar class="w-3/12" />
            </div>
            <section class="alex flex-col w-9/12 ml-14">
                <div class="m-5 p-5 "> 
                <button class="bg-grotto p-5 rounded-full font-bold border-none shadow-md text-royal drop-shadow-2xl" onClick={toggleDiv}>Nuevo Usuario</button>
                {showDiv &&<ModalForm trigger={popUp} class="bg-grotto p-5 rounded-full font-bold border-none shadow-md text-royal drop-shadow-2xl"  addItemToState={refrescarInformacion} />}
                </div>
                <div class="flex content-center items-center overflow-x-auto overflow-y-auto  shadow-xl sm:rounded-t-xl ml-4">
                    <Tabla
                        usuario={usuario}
                        updateState={refrescarInformacion}
                        id="tabla" /> 
                </div>
            </section>
        </section></>


    )
}
